        [Fact]
        public void GuardarSolicitudPrecintos()
        {

            string base64 = "";

            File documentAttached = new File();
            documentAttached.codigo = 3;
            documentAttached.adjuntoBase64 = "data:application/pdf;base64," + base64;
            documentAttached.nombreAdjunto = "CARTA-SOLICITUD-3.pdf";
            documentAttached.tipoAdjunto = "application/pdf";

            SaveRequestSeals datos = new SaveRequestSeals();
            datos.code = 3;
            datos.amount = 15;
            datos.speciesSubspecies = 30145;
            datos.initialCode = _fixture.Create<int>();
            datos.finalCode = 15;
            datos.lengthMinor = _fixture.Create<int>();
            datos.lengthGreater = _fixture.Create<int>();
            datos.consignMentdate = DateTime.Now;
            datos.observations = "observations";
            datos.response = "response";
            datos.consignment = _fixture.Create<bool>();
            datos.documentAttached = documentAttached;
            datos.documentSupportConsignment = documentAttached;
            datos.documentSupport = documentAttached;

            var r = gestionPrencintosNacionalesDirector.GuardarSolicitudPrecintos(datos);
            Assert.True(r != null);

            gestionPrencintosNacionalesDirector.ControllerContext.HttpContext.Session.SetString("token", token);

            r = gestionPrencintosNacionalesDirector.GuardarSolicitudPrecintos(datos);
